CREATE TABLE `Users` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` VARCHAR(255) NOT NULL, `password` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL UNIQUE, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, UNIQUE (`email`));
CREATE TABLE `Devices` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `description` VARCHAR(255) NOT NULL, `switch_on` TINYINT(1) NOT NULL DEFAULT 0, `on_line` TINYINT(1) NOT NULL DEFAULT 0, `created_at` DATETIME NOT NULL, `updated_at` DATETIME NOT NULL, `user_id` INTEGER REFERENCES `Users` (`id`) ON DELETE SET NULL ON UPDATE CASCADE);
